import React, { useState, useContext, useEffect } from 'react';
import LogContext from '../../context/log/logContext';
import M from 'materialize-css/dist/js/materialize.min.js';

const AddLogModal = () => {
  const logContext = useContext(LogContext);

  const { addLog, current, clearCurrent, updateLog } = logContext;

  useEffect(() => {
    if (current !== null) {
      setLog(current);
    } else {
      setLog({
        message: '',
        kilo: '',
        attention: false,
        launderer: '',
      });
    }
  }, [logContext, current]);

  const [log, setLog] = useState({
    message: '',
    kilo: '',
    attention: false,
    launderer: '',
  });

  const { message, kilo, attention, launderer } = log;

  const onChange = (e) => {
    setLog({ ...log, [e.target.name]: e.target.value });
  };

  const onCheck = (e) => {
    setLog({ ...log, [e.target.name]: e.target.checked });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    if (current === null) {
      addLog(log);
    } else {
      updateLog(log);
    }

    if (message === '' || kilo === '' || launderer === '') {
      M.toast({ html: 'Please enter all fields' });
    } else if (current) {
      console.log(log);
      M.toast({ html: 'Log updated!' });
    } else {
      console.log(log);
      M.toast({ html: `Log added` });
    }

    clearAll();
  };

  const clearAll = () => {
    clearCurrent();
  };

  return (
    <div id='log-form-modal' className='modal'>
      <div className='modal-content'>
        <h4>{current ? 'Edit Laundry Log' : 'Add Laundry Log'}</h4>
        <div className='row'>
          <form className='col s12' onSubmit={onSubmit}>
            <div className='row'>
              <div className='input-field col s12'>
                <textarea
                  id='message'
                  name='message'
                  value={message}
                  onChange={onChange}
                  className='materialize-textarea'
                ></textarea>
                <label htmlFor='message' className={current ? 'active' : ''}>
                  Message..
                </label>
              </div>
            </div>
            <div className='row'>
              <div className='input-field col s3'>
                <input
                  id='kilo'
                  name='kilo'
                  type='number'
                  value={kilo}
                  onChange={onChange}
                />
                <label htmlFor='kilo' className={current ? 'active' : ''}>
                  Kilo
                </label>
              </div>
            </div>
            <div className='row'>
              <div className='input-field col s6'>
                <select
                  className='browser-default'
                  name='launderer'
                  value={launderer}
                  onChange={onChange}
                  as='select'
                >
                  <option value='' disabled>
                    Choose Launderer
                  </option>
                  <option value='Jenny Smith'>Jenny Smith</option>
                  <option value='Carla Williams'>Carla Williams</option>
                </select>
              </div>
            </div>
            <div className='row' style={{ marginLeft: '0' }}>
              <p>
                <label>
                  <input
                    id='indeterminate-checkbox'
                    type='checkbox'
                    checked={attention ? false : true}
                    name='attention'
                    // value={attention}
                    onChange={onCheck}
                    // checked={true}
                  />
                  <span>Urgent</span>
                </label>
              </p>
            </div>
          </form>
        </div>
      </div>
      <div className='modal-footer'>
        <a
          href='#!'
          className='modal-close waves-effect waves-green btn'
          onClick={onSubmit}
        >
          Submit
        </a>
      </div>
    </div>
  );
};

export default AddLogModal;
