import React, { useState, useContext, useEffect } from 'react';
import LogContext from '../../context/log/logContext';
import M from 'materialize-css/dist/js/materialize.min.js';

const AddLogModal = () => {
  const logContext = useContext(LogContext);

  const { addLog } = logContext;

  const [message, setMessage] = useState('');
  const [kilo, setKilo] = useState('');
  const [attention, setAttention] = useState(false);
  const [launderer, setLaunderer] = useState('');

  const newLog = {
    message,
    kilo,
    attention,
    launderer,
    date: new Date(),
  };

  const onSubmit = (e) => {
    e.preventDefault();
    logContext.addLog(newLog);

    if (message === '' || kilo === '' || launderer === '') {
      M.toast({ html: 'Please enter all fields' });
    } else {
      addLog(newLog);
      console.log(newLog);
      M.toast({ html: `Log added` });
    }

    setMessage('');
    setKilo('');
    setLaunderer('');
    setAttention(false);
  };

  return (
    <div id='add-log-modal' className='modal'>
      <div className='modal-content'>
        <h4>Add Laundry Log</h4>
        <div className='row'>
          <form className='col s12' onSubmit={onSubmit}>
            <div className='row'>
              <div className='input-field col s12'>
                <textarea
                  id='message'
                  name='message'
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  className='materialize-textarea'
                ></textarea>
                <label htmlFor='message'>Message..</label>
              </div>
            </div>
            <div className='row'>
              <div className='input-field col s3'>
                <input
                  id='kilo'
                  name='kilo'
                  type='number'
                  value={kilo}
                  onChange={(e) => setKilo(e.target.value)}
                />
                <label htmlFor='kilo'>Kilo</label>
              </div>
            </div>
            <div className='row'>
              <div className='input-field col s6'>
                <select
                  className='browser-default'
                  name='launderer'
                  value={launderer}
                  onChange={(e) => setLaunderer(e.target.value)}
                >
                  <option value='' disabled>
                    Choose Launderer
                  </option>
                  <option value='Jenny Smith'>Jenny Smith</option>
                  <option value='Carla Williams'>Carla Williams</option>
                </select>
              </div>
            </div>
            <div className='row' style={{ marginLeft: '0' }}>
              <p>
                <label>
                  <input
                    id='indeterminate-checkbox'
                    type='checkbox'
                    checked={attention}
                    onChange={(e) => setAttention(!attention)}
                  />
                  <span>Urgent</span>
                </label>
              </p>
            </div>
          </form>
        </div>
      </div>
      <div className='modal-footer'>
        <a
          href='#!'
          className='modal-close waves-effect waves-green btn'
          onClick={onSubmit}
        >
          Submit
        </a>
      </div>
    </div>
  );
};

export default AddLogModal;
