import { GET_LOGS, SET_CURRENT, SET_LOADING, LOGS_ERROR } from './types';

export const getLogs = () => async (dispatch) => {
  try {
    setLoading();

    const res = await fetch('/api/logs');
    const data = await res.json();

    dispatch({
      type: GET_LOGS,
      payload: data,
    });
  } catch (err) {
    dispatch({
      type: LOGS_ERROR,
      payload: err.response.statusText,
    });
  }
};

export const setCurrent = (log) => {
  return {
    type: SET_CURRENT,
    payload: log,
  };
};

export const setLoading = () => {
  return {
    type: SET_LOADING,
  };
};
