import React, { useReducer } from 'react';
import axios from 'axios';
import LogContext from './logContext';
import logReducer from './logReducer';
import {
  ADD_LOG,
  DELETE_LOG,
  SET_CURRENT,
  CLEAR_CURRENT,
  FILTER_LOGS,
  CLEAR_FILTER,
  LOG_ERROR,
} from '../types';

const LogState = (props) => {
  const initialState = {
    logs: [],
    current: null,
    filtered: null,
    error: null,
  };

  const [state, dispatch] = useReducer(logReducer, initialState);

  // Add Log
  const addLog = async (log) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    try {
      const res = await axios.post('/api/logs', config);

      dispatch({ type: ADD_LOG, payload: res.data });
    } catch (err) {
      dispatch({ type: LOG_ERROR, payload: err.response.msg });
    }
  };

  // Delete Log
  const deleteLog = (id) => {
    dispatch({ type: DELETE_LOG, payload: id });
  };

  // Set Current Log
  const setCurrent = (log) => {
    dispatch({ type: SET_CURRENT, payload: log });
  };

  // Clear Current Log
  const clearCurrent = () => {
    dispatch({ type: CLEAR_CURRENT });
  };

  // Update Log

  // Filter Log
  const filterLogs = (text) => {
    dispatch({ type: FILTER_LOGS, payload: text });
  };

  // Clear Filter
  const clearFilter = () => {
    dispatch({ type: CLEAR_FILTER });
  };

  return (
    <LogContext.Provider
      value={{
        logs: state.logs,
        current: state.current,
        filtered: state.filtered,
        error: state.error,
        addLog,
        deleteLog,
        setCurrent,
        clearCurrent,
        filterLogs,
        clearFilter,
      }}
    >
      {props.children}
    </LogContext.Provider>
  );
};

export default LogState;
