import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  //   USER_LOADED,
  //   AUTH_ERROR,
  //   LOGIN_SUCCESS,
  //   LOGIN_FAIL,
  //   LOGOUT,
  //   CLEAR_ERRORS,
} from './types';

const initialState = {
  token: null,
  isAuthenticated: false,
  user: null,
  loading: false,
  error: null,
};

export default (state = initialState, action) => {
  switch (action.type) {
    case REGISTER_SUCCESS:
      localStorage.setItem('token', action.payload.token);
      return {
        ...state,
        ...action.payload,
        isAuthenticated: true,
        loading: false,
      };
    case REGISTER_FAIL:
      localStorage.removeItem('token');
      return {
        ...state,
        token: null,
        isAuthenticated: false,
        loading: false,
        user: null,
        error: action.payload,
      };
    default:
      return state;
  }
};
