import {
  GET_LAUNDERERS,
  ADD_LAUNDERER,
  DELETE_LAUNDERER,
  LAUNDERER_ERROR,
} from '../types';

export default (state, action) => {
  switch (action.type) {
    case GET_LAUNDERERS:
      return {
        ...state,
        launderers: action.payload,
        // loading: false,
      };
    case ADD_LAUNDERER:
      return {
        ...state,
        launderers: [...state.launderers, action.payload],
        // loading: false,
      };
    default:
      return state;
  }
};
