import React, { useState } from 'react';

const AddLaundererModal = () => {
  const [launderer, setLaunderer] = useState({
    firstName: '',
    lastName: '',
  });

  const { firstName, lastName } = launderer;

  // setLaunderer({ ...launderer, [e.target.name]: e.target.value });

  const onChange = (e) => {
    setLaunderer({ ...launderer, [e.target.name]: e.target.value });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    console.log(e.target);
  };

  return (
    <div id='add-launderer-modal' className='modal'>
      <div className='modal-content'>
        <h4>Fill Launderer Name</h4>
        <div className='row'>
          <form className='col s12' onSubmit={onSubmit}>
            <div className='row'>
              <div className='input-field col s6'>
                <input
                  id='first_name'
                  type='text'
                  name='firstName'
                  value={firstName}
                  onChange={onChange}
                />
                <label htmlFor='first_name'>First Name</label>
              </div>
              <div className='input-field col s6'>
                <input
                  id='last_name'
                  type='text'
                  name='lastName'
                  value={lastName}
                  onChange={onChange}
                />
                <label htmlFor='last_name'>Last Name</label>
              </div>
            </div>
          </form>
        </div>
      </div>
      <div className='modal-footer'>
        <a
          href='#!'
          onClick={onSubmit}
          className='modal-close waves-effect waves-green btn'
        >
          Submit
        </a>
      </div>
    </div>
  );
};

export default AddLaundererModal;
