import React, { useReducer } from 'react';
import uuid from 'react-uuid';
import LogContext from './logContext';
import logReducer from './logReducer';
import {
  ADD_LOG,
  DELETE_LOG,
  SET_CURRENT,
  CLEAR_CURRENT,
  UPDATE_LOG,
  FILTER_LOGS,
  CLEAR_FILTER,
} from '../types';

const LogState = (props) => {
  const initialState = {
    logs: [
      {
        id: 1,
        message: "Beka's Clothes pick up tomorrow 7am",
        kilo: 2,
        attention: false,
        launderer: 'Jenny Smith',
      },
      {
        id: 2,
        message: "Sheila's Clothes pick up tomorrow 9am",
        kilo: 2,
        attention: true,
        launderer: 'Jenny Smith',
      },
      {
        id: 3,
        message: 'Brandon Clothes pick up on Saturday',
        kilo: 1,
        attention: true,
        launderer: 'Carla Williams',
      },
    ],
    current: null,
    filtered: null,
  };

  const [state, dispatch] = useReducer(logReducer, initialState);

  // Add Log
  const addLog = (log) => {
    log.id = uuid();
    dispatch({ type: ADD_LOG, payload: log });
  };

  // Delete Log
  const deleteLog = (id) => {
    dispatch({ type: DELETE_LOG, payload: id });
  };

  // Set Current Log
  const setCurrent = (log) => {
    dispatch({ type: SET_CURRENT, payload: log });
  };

  // Clear Current Log
  const clearCurrent = (log) => {
    dispatch({ type: CLEAR_CURRENT });
  };

  // Update Log

  // Filter Log
  const filterLogs = (text) => {
    dispatch({ type: FILTER_LOGS, payload: text });
  };

  // Clear Filter
  const clearFilter = () => {
    dispatch({ type: CLEAR_FILTER });
  };

  return (
    <LogContext.Provider
      value={{
        logs: state.logs,
        filtered: state.filtered,
        addLog,
        deleteLog,
        filterLogs,
        clearFilter,
      }}
    >
      {props.children}
    </LogContext.Provider>
  );
};

export default LogState;
